import React, { useRef, useEffect } from 'react';
import './App.css';
import Webcam from 'react-webcam';
import { Pose } from '@mediapipe/pose';
import * as tf from '@tensorflow/tfjs';

function App() {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const pose = new Pose({
      locateFile: (file) => {
        return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
      }
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5
    });

    pose.onResults(onResults);

    if (webcamRef.current && webcamRef.current.video) {
      webcamRef.current.video.addEventListener('loadeddata', (event) => {
        startCamera(pose);
      });
    }

    function startCamera(pose) {
      const video = webcamRef.current.video;

      const onFrame = async () => {
        if (video.readyState === 4) {
          await pose.send({ image: video });
        }
        requestAnimationFrame(onFrame);
      };

      onFrame();
    }

    function onResults(results) {
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      webcamRef.current.video.width = videoWidth;
      webcamRef.current.video.height = videoHeight;

      canvasRef.current.width = videoWidth;
      canvasRef.current.height = videoHeight;

      const ctx = canvasRef.current.getContext("2d");
      ctx.clearRect(0, 0, videoWidth, videoHeight);
      ctx.drawImage(webcamRef.current.video, 0, 0, videoWidth, videoHeight);

      if (results.poseLandmarks) {
        drawLandmarks(ctx, results.poseLandmarks);
        drawSkeleton(ctx, results.poseLandmarks);
      }
    }

    function drawLandmarks(ctx, landmarks) {
      landmarks.forEach(({ x, y, visibility }) => {
        if (visibility > 0.2) {
          ctx.beginPath();
          ctx.arc(x * canvasRef.current.width, y * canvasRef.current.height, 4, 0, 2 * Math.PI);
          ctx.fillStyle = "white";
          ctx.fill();
        }
      });
    }

    function drawSkeleton(ctx, landmarks) {
      const connections = [
        [11, 13], [13, 15], [12, 14], [14, 16],
        [11, 12], [5, 6], [5, 11], [6, 12], [5, 7], [7, 9],
        [6, 8], [8, 10], [9, 10]
      ];

      connections.forEach(([start, end]) => {
        const startLandmark = landmarks[start];
        const endLandmark = landmarks[end];

        if (startLandmark.visibility > 0.2 && endLandmark.visibility > 0.2) {
          ctx.beginPath();
          ctx.moveTo(startLandmark.x * canvasRef.current.width, startLandmark.y * canvasRef.current.height);
          ctx.lineTo(endLandmark.x * canvasRef.current.width, endLandmark.y * canvasRef.current.height);
          ctx.strokeStyle = "white";
          ctx.lineWidth = 3;
          ctx.stroke();
        }
      });
    }
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <Webcam
          ref={webcamRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zIndex: 9,
            width: 640,
            height: 480,
          }}
        />
        <canvas
          ref={canvasRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zIndex: 9,
            width: 640,
            height: 480,
          }}
        />
      </header>
    </div>
  );
}

export default App;
