import { subtract, dot, norm, degrees } from 'mathjs';

function calculateAngle(a, b, c) {
  const ba = subtract(a, b);
  const bc = subtract(c, b);

  const cosineAngle = dot(ba, bc) / (norm(ba) * norm(bc));
  const clippedCosineAngle = Math.max(-1, Math.min(1, cosineAngle));
  
  const angleRad = Math.acos(clippedCosineAngle);
  const angleDeg = degrees(angleRad);

  return angleDeg;
}
