import { dot, norm, acos, unit } from 'mathjs';

export const calculateAngle = (a, b, c) => {
  const ba = [a[0] - b[0], a[1] - b[1]];
  const bc = [c[0] - b[0], c[1] - b[1]];

  const cosineAngle = dot(ba, bc) / (norm(ba) * norm(bc));
  const clippedCosineAngle = Math.max(-1, Math.min(1, cosineAngle));
  
  const angleRad = acos(clippedCosineAngle);
  const angleDeg = unit(angleRad, 'rad').toNumber('deg');

  return angleDeg;
};
